INSERT INTO `wp_posts` VALUES (13,1,'2021-08-07 09:09:13','2021-08-07 00:09:13','<!-- wp:image {\"id\":88,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://wordpressflogsite.local/wp-content/uploads/2021/08/moudoku_hukiya-3.jpg\" alt=\"\" class=\"wp-image-88\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>分類</strong>：両生綱無尾目ヤドクガエル科フキヤガエル属</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分布</strong>：コロンビア</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体長</strong>：5〜6cm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体色</strong>：画像のような黄色の他、橙色、ミント色など。生息地によって異なる。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>毒</strong>：あり</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>説明</strong>：その名の通り、コロンビアの原住民が毒を吹き矢の先に塗り、狩猟に利用した事が名前の由来となっている。バトラコトキシンという強い毒素を持つ。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>バトラコトキシンが体内に入ると筋肉を収縮させるため、心臓発作を引き起こす。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>その毒の強さは地球上の生物で最強と言われる。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ただし、毒の強さは食べるエサに関係していると言われ、水族館で飼育されている個体、日本でペットとして飼われている個体は毒性は低くなるとの事。</p>\n<!-- /wp:paragraph -->','モウドクフキヤガエル','','publish','open','open','','%e3%83%a2%e3%82%a6%e3%83%89%e3%82%af%e3%83%95%e3%82%ad%e3%83%a4%e3%82%ac%e3%82%a8%e3%83%ab','','','2021-08-10 19:04:38','2021-08-10 10:04:38','',0,'http://wordpressflogsite.local/?p=13',0,'post','',0);
