INSERT INTO `wp_posts` VALUES (90,1,'2021-08-10 18:29:40','2021-08-10 09:29:40','<!-- wp:image {\"id\":88,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://wordpressflogsite.local/wp-content/uploads/2021/08/moudoku_hukiya-3.jpg\" alt=\"\" class=\"wp-image-88\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>分類</strong>：両生綱無尾目ヤドクガエル科フキヤガエル属</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分布</strong>：コロンビア</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体長</strong>：5〜6cm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体色</strong>：画像のような黄色の他、橙色、ミント色など。生息地によって異なる。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>毒</strong>：あり</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>説明</strong>：その名の通り、コロンビアの原住民が毒を吹き矢の先に塗り、狩猟に利用した事が名前の由来となっている。バトラコトキシンという強い毒素を持つ。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>バトラコトキシンが体内に入ると筋肉を収縮させるため、心臓発作を引き起こす。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>その毒の強さは地球上の生物で最強と言われる。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ただし、毒の強さは食べるエサに関係していると言われ、水族館で飼育されている個体、日本でペットとして飼われている個体は毒性は低くなるとの事。</p>\n<!-- /wp:paragraph -->','モウドクフキヤガエル','','inherit','closed','closed','','13-revision-v1','','','2021-08-10 18:29:40','2021-08-10 09:29:40','',13,'http://wordpressflogsite.local/?p=90',0,'revision','',0);
