INSERT INTO `wp_posts` VALUES (108,1,'2021-08-13 07:50:25','2021-08-12 22:50:25','<!-- wp:image {\"id\":109,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://wordpressflogsite.local/wp-content/uploads/2021/08/akame-kiji.jpg\" alt=\"\" class=\"wp-image-109\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>分類：</strong>アマガエル科アカメアマガエル属</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分布：</strong>グアテマラ、コスタリカ、ニカラグア、パナマ、ベリーズ、ホンジュラス、メキシコ南部</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体長：</strong>3〜7cm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体色：</strong>背中側がグリーン、四肢の半分〜脇腹辺りがブルーとイエロー、腹側は白、瞳は赤、と非常に特徴的</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>毒：</strong>なし</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>説明：</strong>熱帯雨林や二次林に生息する。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>夜行性で、日中は葉っぱの裏側に張り付き体を縮めて眠っている。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>そのため、背中側のグリーンしか見えないので葉との区別がつきづらくなり、敵に見つかりにくくなる。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>日本ではペットとして輸入されている。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>カラフルなカエルは毒を持つ事が多いが、アカメアマガエルは毒を持たない。</p>\n<!-- /wp:paragraph -->','アカメアマガエル','','publish','open','open','','%e3%82%a2%e3%82%ab%e3%83%a1%e3%82%a2%e3%83%9e%e3%82%ac%e3%82%a8%e3%83%ab','','','2021-08-13 07:50:25','2021-08-12 22:50:25','',0,'http://wordpressflogsite.local/?p=108',0,'post','',0);
