INSERT INTO `wp_posts` VALUES (84,1,'2021-08-10 18:02:30','2021-08-10 09:02:30','<!-- wp:image {\"id\":80,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://wordpressflogsite.local/wp-content/uploads/2021/08/nihon_ama-2.jpg\" alt=\"\" class=\"wp-image-80\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>分類</strong>：両生綱無尾目アマガエル科アマガエル属</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分布</strong>：日本、朝鮮半島、中国東部</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体長</strong>：2.0〜4.5cm程</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体色</strong>：黄緑</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>毒</strong>：あり</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>説明</strong>：おなじみニホンアマガエル。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>体色は黄緑色だが、まだら模様の灰褐色に変えることができる。（保護色）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>なお、毒性がある事はあまり知られていないが、体を細菌から守るため、皮膚の粘膜から毒が分泌されている。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>日本人なら見慣れていてなじみのあるカエルだが、何気にキレイな色をしている事にお気づきだろうか…？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>という事で当ずかんに掲載する事にした。</p>\n<!-- /wp:paragraph -->','ニホンアマガエル','','inherit','closed','closed','','15-revision-v1','','','2021-08-10 18:02:30','2021-08-10 09:02:30','',15,'http://wordpressflogsite.local/?p=84',0,'revision','',0);
