INSERT INTO `wp_posts` VALUES (87,1,'2021-08-10 18:25:33','2021-08-10 09:25:33','<!-- wp:image {\"id\":85,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://wordpressflogsite.local/wp-content/uploads/2021/08/madara_yadoku2.jpg\" alt=\"\" class=\"wp-image-85\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>分類：</strong>ヤドクガエル科ヤドクガエル属</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分布：</strong>コスタリカ、コロンビア、ニカラグア南部、パナマ、ホンジュラスなど</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体長：</strong>2.5〜4cm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体色：</strong>生息地域によって変わる。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>画像のようなメタリックグリーンとブラックのほか、ブルー＆ブラック、ブラック＆ゴールド、ブロンズ＆グリーンなどの体色の個体も存在する。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>毒：</strong>あり</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>説明：</strong>自宅の庭で子供が見つけたら喜んで捕まえてしまいそうな見た目をしているが、皮膚に猛毒を持つため決して触れてはいけない。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>その名のとおり、原住民がこのカエルの毒を使って毒矢を作った事が名前の由来とされている。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>マダラヤドクガエルが現地で主食にしているアリの毒が体内で蓄積され毒素となるらしい。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>よって、飼育されているものは毒性が低くなるか無毒となるとの事。</p>\n<!-- /wp:paragraph -->','マダラヤドクガエル','','inherit','closed','closed','','11-revision-v1','','','2021-08-10 18:25:33','2021-08-10 09:25:33','',11,'http://wordpressflogsite.local/?p=87',0,'revision','',0);
