INSERT INTO `wp_posts` VALUES (93,1,'2021-08-10 18:45:57','2021-08-10 09:45:57','<!-- wp:image {\"id\":91,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://wordpressflogsite.local/wp-content/uploads/2021/08/kiobi_yadoku-1.jpg\" alt=\"\" class=\"wp-image-91\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>分類：</strong>ヤドクガエル科ヤドクガエル属</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分布：</strong>ガイアナ、コロンビア東部、ブラジル北部、ベネズエラ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体長：</strong>3〜3.8cm</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>体色：</strong>黄色に黒の斑点</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>毒：</strong>あり</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>説明：</strong>ヤドクガエルの名の通り、強い毒性を持っている。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>バトラコトキシンというその毒は、0.00001gで人を倒すことができるという。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>なお、他の毒ガエルにも言える事だが生まれた時から有毒なわけではない。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>アリなどの摂取するエサから毒素を体内に蓄積していき、徐々に有毒化していく。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>日本ではペットとして輸入されている。</p>\n<!-- /wp:paragraph -->','キオビヤドクガエル','','inherit','closed','closed','','9-revision-v1','','','2021-08-10 18:45:57','2021-08-10 09:45:57','',9,'http://wordpressflogsite.local/?p=93',0,'revision','',0);
